CREATE DATABASE camory;

USE camory;

CREATE TABLE users (
    id CHAR(36) PRIMARY KEY NOT NULL,
    email VARCHAR(255) NOT NULL,
    password VARCHAR(255) NOT NULL,
    username VARCHAR(255) NOT NULL
);


CREATE TABLE decks (
    id CHAR(36) PRIMARY KEY NOT NULL,
    user_id CHAR(36) NOT NULL ,
    name VARCHAR(255) NOT NULL,
    new_cards_per_day INT NOT NULL,
    FOREIGN KEY (user_id) REFERENCES users(id) ON UPDATE CASCADE ON DELETE CASCADE
);


CREATE TABLE cards(
	id CHAR(36) PRIMARY KEY NOT NULL,
    vocabulary VARCHAR(255) NOT NULL,
    clue VARCHAR(255) NOT NULL,
    asset LONGTEXT NOT NULL,
    definition VARCHAR(255) NOT NULL,
    description VARCHAR(255) NOT NULL
);


CREATE TABLE user_cards (
    id CHAR(36) PRIMARY KEY NOT NULL,
    deck_id CHAR(36) NOT NULL,
    card_id CHAR(36) NOT NULL,
    status VARCHAR(255) NOT NULL,
    ease DOUBLE NOT NULL DEFAULT 2.5,
    fails INT NOT NULL DEFAULT 0,
    streak INT NOT NULL DEFAULT 0,
    review_time DATETIME NOT NULL DEFAULT '2000-01-01 00:00:00',
    due DATETIME NOT NULL DEFAULT '2000-01-01 00:00:00',
    FOREIGN KEY (deck_id) REFERENCES decks(id) ON DELETE CASCADE ON UPDATE CASCADE,
    FOREIGN KEY (card_id) REFERENCES cards(id) ON DELETE CASCADE ON UPDATE CASCADE
);


