CREATE TABLE games (
    id CHAR(36) PRIMARY KEY NOT NULL,
    name VARCHAR(255) NOT NULL,
    asset LONGTEXT NOT NULL,
);


CREATE TABLE game_cards( 
    id CHAR(36) PRIMARY KEY NOT NULL,
    game_id CHAR(36) NOT NULL,
    card_id CHAR(36) NOT NULL,
    FOREIGN KEY (game_id) REFERENCES games(id) ON DELETE CASCADE ON UPDATE CASCADE,
    FOREIGN KEY (card_id) REFERENCES cards(id) ON DELETE CASCADE ON UPDATE CASCADE
);


CREATE TABLE game_card_choices (
    game_card_id CHAR(36) NOT NULL,
    answer VARCHAR(255) NOT NULL,
    is_correct BOOLEAN NOT NULL,
    clicked_times INT NOT NULL DEFAULT 0,
    FOREIGN KEY (game_card_id) REFERENCES game_cards(id) ON DELETE CASCADE ON UPDATE CASCADE,
    PRIMARY KEY (game_card_id, answer)
);


CREATE TABLE game_players (
    game_id CHAR(36) NOT NULL,
    user_id CHAR(36) NOT NULL,
    score INT NOT NULL DEFAULT 0,
    correct_answers INT NOT NULL DEFAULT 0, 
    incorrect_answers INT NOT NULL DEFAULT 0,
    FOREIGN KEY (game_id) REFERENCES games(id) ON DELETE CASCADE ON UPDATE CASCADE,
    FOREIGN KEY (user_id) REFERENCES users(id) ON DELETE CASCADE ON UPDATE CASCADE,
    PRIMARY KEY (game_id, user_id)
);